mapper.select:
select *
from question
where klass_seminar_id=#{klassSeminarId} and attendance_id=#{attendanceId}

mapper.update:
update question
set score=#{questionScore}
where id=#{questionId}

mapper.select:
select klass_id
from klass_seminar
where id=#{klassSeminarId}

select seminar_id
from klass_seminar
where id=#{klassSeminarId}

select team_id
from question
where id=#{questionId}

select klass_serial
from klass
where id=#{klassId}

select team_serial
from team
where id=#{teamId}

select student_name
from student
where id=#{studentId}

mapper.update:
update question
set is_selected = 1
where id=#{questionId}

mapper.select:
select *
from seminar_score
where klass_seminar_id=#{klassSeminarId} and team_id=#{teamId}

mapper.update:
update seminar_score
set question_score=#{questionScore}
where klass_seminar_id=#{klassSeminarId} and team_id=#{teamId}

update seminar_score
set total_score=#{totalScore}
where klass_seminar_id=#{klassSeminarId} and team_id=#{teamId}

mapper.select:
select round_id
from seminar
where id=#{seminarId}

mapper.update:
update round_score
set total_score=#{totalScore1},presentation_score=#{presentationScore1}
    ,question_score=#{questionScore1},report_score=#{reportScore1}
where round_id=#{roundId} and team_id=#{teamId}

mapper.select:
select team_id
from team_student
where student_id=#{studentId}
