mapper.update:
update round
set presentation_score_method=#{presentationScoreMethod},
    report_score_method=#{reportScoreMethod},
    question_score_method=#{questionScoreMethod}
where id=#{roundId}

update round
set presentation_score_method=#{presentationScoreMethod},
    report_score_method=#{reportScoreMethod},
    question_score_method=#{questionScoreMethod}
where id=#{roundId}

mapper.select:
select round_id
from round
where course_id=#{courseId} and round_serial=#{order}
