mapper.select:
select student_id
from team_student
where team_id=#{teamId}

select team_id
from team_student
where student_id=#{studentId}

select klass_id
from team
where id=#{teamId}

select max(team_serial)
from team
where klass_id=#{klassId}

select klass_id
from klass_team
where team_id=#{teamId}

mapper.update:
update team
set status=#{status}
where id=#{teamId}

mapper.insert:
insert into klass_team(
                       klass_id,
                       team_id
) values (
          #{klassId},
          #{teamId}
                 )

insert into team_student(
                         team_id,
                         student_id
) values (
          #{teamId},
          #{studentId}
                 )

insert into klass_student(
                          klass_id,
                          student_id,
                          course_id
) values (
          #{klassId},
          #{studentId},
          #{courseId}
                 )

mapper.select:
select team_serial
from team
where id=#{teamId}
